import React from 'react';

class Home extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      searchText: "",
      searchResults: [],
      totalResults: 0,
    }
  }

  searchButtonClicked = () => {
    //make API request 

    const text = this.state.searchText.trim();

    fetch(`http://localhost:8080/api?searchText=${encodeURIComponent(text)}`, {
      method: 'GET'})
      .then((response) => {
        return response.json();
      })
      .then((json) => {

        console.log('JSON received');
        console.log(json);

        //sort for worst ratings here 

        let parkings = json.businesses; 

        function compare(a, b) {
          if (a.rating > b.rating) return 1;
          if (b.rating > a.rating) return -1;

          return 0;
        }

        parkings.sort(compare);

        this.setState({
          searchResults: json.businesses,
          totalResults: json.total,
        });

      })
      .catch((error) => {

        console.log("There was an error: ");
        console.log(error);

      });
  }

  searchTextChanged = (event) => {
    this.setState({
      searchText: event.target.value
    });
  }

  //Show a list of parking lots from Yelp in that location with the address, an image if available, star rating, review count, and link to the Yelp page

  render() {
    return(
      <div style={{padding: 20}}>

        <h1>AirGarage Homework Assignment</h1>

        <input 
          onChange={this.searchTextChanged}
          type="text"
          id="name"
          name="name"
          placeholder={"Enter Search Here"} 
          style={{height: 50, borderRadius: 5, fontSize: 18, marginRight: 15}}
        />

        <button 
          onClick={this.searchButtonClicked}
          style={{height: 50}}
        >
          Submit Search
        </button>



          {this.state.searchResults.map((value, index) => {

            //score = ( number of reviews * rating ) / (number of reviews + 1)

            const score = (value.review_count * value.rating) / ( value.review_count + 1);

            return (
              <div>
                <p style={{fontWeight: 'bold'}} >Name: {value.name}</p>
                <p>Address: {value.location.address1}, {value.location.city}, {value.location.state}</p>
                <img style={{width: 100, height: 'auto'}} src={value.image_url} /> 
                <p>Star rating: {value.rating}</p>
                <p>Review Count: {value.review_count}</p>
                <p>Page Link: <a href={value.url}>Link here</a></p>
                <p>Parking Score: {score} </p>
              </div>
            );
          })}



      </div>



    );
  }
}

export default Home;
